using UnityEngine;\nusing UnityEngine.UI;\nusing UnityEngine.SceneManagement;\n\nnamespace Managers\n{\n    public class UIManager : MonoBehaviour\n    {\n        public static UIManager Instance;\n\n        [Header("UI Panels")]\n        public GameObject mainMenuPanel;\n        public GameObject pathOptionsPanel;\n        public GameObject qrCodePanel;\n\n        [Header("QR Code Display")]\n        public Image qrCodeImage;\n\n        void Awake()\n        {\n            if (Instance == null)\n            {\n                Instance = this;\n            }\n            else\n            {\n                Destroy(gameObject);\n            }\n        }\n\n        void Start()\n        {\n            // Initially show the main menu\n            ShowMainMenu();\n        }\n\n        public void ShowMainMenu()\n        {\n            mainMenuPanel.SetActive(true);\n            pathOptionsPanel.SetActive(false);\n            qrCodePanel.SetActive(false);\n        }\n\n        public void ShowPathOptions()\n        {\n            mainMenuPanel.SetActive(false);\n            pathOptionsPanel.SetActive(true);\n            qrCodePanel.SetActive(false);\n        }\n\n        public void ShowQRCode(Texture2D qrTexture)\n        {\n            mainMenuPanel.SetActive(false);\n            pathOptionsPanel.SetActive(false);\n            qrCodePanel.SetActive(true);\n            if (qrCodeImage != null)\n            {\n                qrCodeImage.sprite = Sprite.Create(qrTexture, new Rect(0, 0, qrTexture.width, qrTexture.height), new Vector2(0.5f, 0.5f));\n            }\n        }\n\n        // --- Button Callbacks ---\n        public void OnCreatePathButtonClick()\n        {\n            Debug.Log("Create Path Button Clicked");\n            // Logic to initiate path marking would go here, perhaps by calling a method on PathManager\n            // For now, let's just show path options\n            ShowPathOptions();\n        }\n\n        public void OnFollowPathButtonClick()\n        {\n            Debug.Log("Follow Path Button Clicked");\n            // Logic to open QR scanner or prompt for input\n            // For now, let's show path options\n            ShowPathOptions();\n        }\n\n        public void OnBackToMainMenuButtonClick()\n        {\n            Debug.Log("Back to Main Menu Button Clicked");\n            ShowMainMenu();\n        }\n\n        public void OnGenerateQRCodeButtonClick()\n        {\n            Debug.Log("Generate QR Code Button Clicked");\n            // This would trigger QR generation based on a saved path\n            // Example: PathManager.Instance.GenerateQRCodeForCurrentPath();\n        }\n\n        public void OnScanQRCodeButtonClick()\n        {\n            Debug.Log("Scan QR Code Button Clicked");\n            // This would trigger QR scanning in the AR scene\n            // Example: PathFollower.Instance.StartQRScan();\n        }\n\n        // Event handler for when a QR code is generated and data is ready to be displayed\n        public void HandleOnQRCodeGenerated(Texture2D qrTexture)\n        {\n            ShowQRCode(qrTexture);\n        }\n    }\n}\n