using UnityEngine;\nusing UnityEngine.UI;\nusing UnityEngine.Events;\n\npublic class PathMarkerUI : MonoBehaviour\n{\n    [Header("UI Elements")]\n    public Button startButton;\n    public Button endButton;\n    public Text statusText;\n\n    [Header("Events")]\n    public UnityEvent OnStartRecordingClicked;\n    public UnityEvent OnEndRecordingClicked;\n\n    private void Start()\n    {\n        if (startButton != null)\n        {\n            startButton.onClick.AddListener(() => OnStartRecordingClicked.Invoke());\n        }\n        if (endButton != null)\n        {\n            endButton.onClick.AddListener(() => OnEndRecordingClicked.Invoke());\n        }\n        UpdateStatus("Ready to start path marking.");\n        SetButtonStates(true, false);\n    }\n\n    public void UpdateStatus(string message)\n    {\n        if (statusText != null)\n        {\n            statusText.text = message;\n        }\n    }\n\n    public void SetButtonStates(bool startEnabled, bool endEnabled)\n    {\n        if (startButton != null)\n        {\n            startButton.interactable = startEnabled;\n        }\n        if (endButton != null)\n        {\n            endButton.interactable = endEnabled;\n        }\n    }\n}\n